mapper.insert:
INSERT INTO account (
    member_no
,   bank
,   account_number
,   name
,   is_default
)
VALUES (
    #{memberNo}
,   #{req.bank}
,   #{req.accountNumber}
,   #{req.name}
,   #{isDefault}
)

mapper.update:
UPDATE
    account
SET
    bank = #{req.bank}
,   account_number = #{req.accountNumber}
,   name = #{req.name}
WHERE
    member_no = #{memberNo}
    and account_id = #{req.accountId}
