mapper.insert:
INSERT INTO address (
    member_no
,   name
,   phone
,   address
,   address_detail
,   is_default
)
VALUES (
    #{memberNo}
,   #{req.name}
,   #{req.phone}
,   #{req.address}
,   #{req.addressDetail}
,   #{isDefault}
)

mapper.update:
UPDATE
    address
SET
    name = #{req.name}
,   phone = #{req.phone}
,   address = #{req.address}
,   address_detail = #{req.addressDetail}
WHERE
    member_no = #{memberNo}
and address_id = #{req.addressId}
